import React, { Component } from 'react';
import {connect} from "react-redux"
import * as actions from "./../actions/index"

class TaskSortControl extends Component {

    onSort = (sortBy, sortValue) => {
        this.props.onSort({
            by: sortBy,
            value: sortValue
        });
    }

    render() {
        return (
            <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                <div className="dropdown">
                    <button
                        className="btn btn-primary dropdown-toggle"
                        type="button"
                        id="dropdownMenu1"
                        data-toggle="dropdown"
                        aria-haspopup="true"
                        aria-expanded="true"
                    >
                        Sắp Xếp <span className="fa fa-caret-square-o-down ml-5"></span>
                    </button>
                    <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
                        <li onClick={ () => this.onSort('name', 'asc') } >
                            <a role="button" className={ (this.props.sortBy === 'name' && this.props.sortValue === 'asc') ? 'sort_selected' : '' }>
                                <span className="fa fa-sort-alpha-asc pr-5">
                                    Tên A-Z
                                </span>
                            </a>
                        </li>
                        <li onClick={ () => this.onSort('name', 'desc') } >
                            <a role="button" className={ (this.props.sortBy === 'name' && this.props.sortValue === 'desc') ? 'sort_selected' : '' }>
                                <span className="fa fa-sort-alpha-desc pr-5">
                                    Tên Z-A
                                </span>
                            </a>
                        </li>
                        <li role="separator" className="divider"></li>
                        <li onClick={ () => this.onSort('status', 'desc') } >
                            <a role="button" className={ (this.props.sortBy === 'status' && this.props.sortValue === 'desc') ? 'sort_selected' : '' }>
                                Trạng Thái Kích Hoạt
                            </a>
                        </li>
                        <li onClick={ () => this.onSort('status', 'asc') } >
                            <a role="button" className={ (this.props.sortBy === 'status' && this.props.sortValue === 'asc') ? 'sort_selected' : '' }>
                                Trạng Thái Ẩn
                            </a>
                        </li>
                    </ul>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
	return {
        sort: state.sort
    }
}

const mapDispatchToProps = (dispatch, props) => {
	return {
		onSort: (sort) => {
            dispatch(actions.sortTask(sort))
        }
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskSortControl)
