import * as types from "./../contains/ActionTypes"

const initialState = {
	by: "",
	value: asc
}

const myReducer = (state = initialState, action) => {
	switch (action.type) {
		case types.SORT:
			console.log(action)
			return {
				by: action.sort.by,
				value: action.sort.value
			}
		default:
			return state
	}
}

export default myReducer
