import * as types from "./../contains/ActionTypes"

const initialState = {
	name: "",
	status: -1
}

const myReducer = (state = initialState, action) => {
	switch (action.type) {
		case types.FILTER_TABLE:
			action.status = parseInt(action.filter.status)
			return action.filter
		default:
			return state
	}
}

export default myReducer
